import cv2
import numpy as np
import socketio
import time

# === Koneksi ke server socket.io ===
sio = socketio.Client()

while True:
    try:
        sio.connect('http://127.0.0.1:5000')  # Server.js berjalan di port 5000
        print("âœ… Terhubung ke server.js")
        break
    except:
        print("Menunggu server.js...")
        time.sleep(2)

# === Inisialisasi kamera ===
cap = cv2.VideoCapture(0)
if not cap.isOpened():
    print("âŒ Kamera tidak terdeteksi")
    exit()

print("ðŸ“¸ Deteksi warna aktif...")

while True:
    ret, frame = cap.read()
    if not ret:
        continue

    hsv = cv2.cvtColor(frame, cv2.COLOR_BGR2HSV)

    # === Warna Merah (P0033) ===
    lower_red1 = np.array([0, 120, 70])
    upper_red1 = np.array([10, 255, 255])
    lower_red2 = np.array([170, 120, 70])
    upper_red2 = np.array([180, 255, 255])
    mask_red = cv2.inRange(hsv, lower_red1, upper_red1) + cv2.inRange(hsv, lower_red2, upper_red2)

    # === Warna Hijau (P0105) ===
    lower_green = np.array([40, 50, 50])
    upper_green = np.array([85, 255, 255])
    mask_green = cv2.inRange(hsv, lower_green, upper_green)

    red_area = cv2.countNonZero(mask_red)
    green_area = cv2.countNonZero(mask_green)

    warna = "NONE"
    if red_area > 2000:
        warna = "MERAH"
    elif green_area > 2000:
        warna = "HIJAU"

    # === Kirim ke server.js via socket.io ===
    sio.emit("deteksi-warna", {"color": warna})

    # Opsional preview
    cv2.imshow("Kamera", frame)
    if cv2.waitKey(1) == 27:  # tekan ESC
        break

cap.release()
cv2.destroyAllWindows()
sio.disconnect()
